			var estr = " AS DateTime))";
			var lines = asql.Split(estr);
			foreach(var line in lines)
			{
				var s1 = line.Replace("\n", "").Replace("\r", "").Replace(" ", "").Trim();
				if (s1 == "") continue;

				var sql = line + estr;

				if (sql.Contains("\r\nGO\r\nINSERT INTO"))
				{
					sql = sql.Substring(4);
				}

				//Debug.WriteLine("-------------");
				//Debug.WriteLine(sql);
				//Debug.WriteLine("-------------");
				DB.Execute(sql);
			}


