INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (181, N'Covid19TestingPcrTestOr', N'COVID-19 TESTING: PCR TEST OR SEROLOGY TEST CONSENT FORM GVT LAB AND IMAGING SERVICES INC.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.530' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (182, N'Covid19IsAnInfectiousIllness', N'COVID-19 is an infectious illness caused by a newly discovered coronavirus. For many, the illness is mild or does not produce symptoms; however, the elderly, those with underlying medical problems (such as heart disease, diabetes, chronic respiratory disease, and cancer), and those who are constantly in contact with others in a close manner are more likely to develop a serious illness that may result in hospitalization or even death. We, at GVT LAB & IMAGING SERVICES INC., are offering you a COVID-19 test (Serology and/or PCR test) in order to screen for, prevent, and expedite the recovery of our society.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.530' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (183, N'PrivacyNotice', N'Privacy Notice', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.530' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (184, N'WeAreCollectingYourTest', N'We are collecting your test sample and other personal information to determine if you have COVID-19 and to provide you with information on your results. Your personal information will be collected for the purpose of identification, matching the sample to the individual and creating a test label. You are not legally required to provide this data, but if you do not provide this information, we cannot test you. The only people who will have access to private information, such as your name and medical information, will be GVT LAB & IMAGING SERVICES INC., and if applicable, the following entities: Health Canada, local public health unit, or their contractors to conduct disease investigations or other public health activities, or other persons authorized by law. Please note, GVT LAB & IMAGING SERVICES INC. is undertaking the work on behalf of the Ministry of Education and will be sharing aggregated information with the ministry, but not personal information.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.530' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (185, N'PleaseCarefullyReadAndSign', N'Please Carefully Read And Sign The Following Informed Consent:', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.530' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (186, N'ThroughYourSignatureBelowYou', N'Through your signature below, you certify that you have read and understood the attached COVID-19 Post-test Instructions, and that you have had the opportunity to ask questions about the test. You have also been informed of the risks and benefits associated with the test, including the possibility of slight discomfort or an incorrect result. If you undergo testing, you acknowledge that you did so voluntarily. If you decline testing, you understand that you may carry or transmit COVID-19, even if you do not have symptoms.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.530' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (187, N'YouAlsoUnderstandThatGvt', N'You also understand that GVT LAB & IMAGING SERVICES INC. is not liable for the test results.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.533' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (188, N'IAuthorizeThisCovid19Testing', N'I authorize this COVID-19 testing unit to conduct collection and testing for COVID-19 for Serology or PCR test, as ordered by an authorized medical provider, public health official or employer.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.533' AS DateTime))
