INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (190, N'IAcknowledgeThatAPositive', N'I acknowledge that a positive test result is an indication that I must self-isolate and follow any other directions as prescribed by local health officials in an effort to avoid infecting others.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.533' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (191, N'IUnderstandTheTestingUnit', N'I understand the testing unit is not acting as my medical provider, this testing does not replace treatment by my medical provider, and I assume complete and full responsibility to take appropriate action with regards to my test results. I agree, that I will seek medical advice, care and treatment from my medical provider if I have questions or concerns, or if my condition worsens.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.533' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (192, N'IUnderstandThatAsWith', N'I understand that, as with any medical test, there is the potential for a false positive or false negative COVID-19 test result. Negative results of the test indicate the lack of current infection, but do not mean that I cannot be infected in the future.', N'', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:08.533' AS DateTime))
