INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (203, N'AsymptomaticSpreadOfCovid19Has', N'On sait que la propagation asymptomatique de la Covid-19 est l''un des grands contributeurs à la pandémie mondiale à laquelle nous sommes confrontés actuellement. Certaines études révèlent que le taux d''infections asymptomatiques est proche de 50 %, en particulier chez les jeunes. Le dépistage des personnes asymptomatiques est un élément clé pour garder la maladie sous contrôle, principalement dans des environnements de groupe comme les écoles, les espaces de travail de proximité et les milieux industriels. Des études montrent que les personnes qui ne présentent aucun symptôme du nouveau coronavirus peuvent encore propager l''infection et sont en fait l''un des principaux contributeurs à la croissance rapide des cas transmis dans les communautés.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.797' AS DateTime))
