INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (228, N'YesAsymptomaticCovid19TestingIs', N'Oui, les tests asymptomatiques de la Covid-19 sont sécuritaires et volontaires. La procédure est effectuée par des infirmières autorisées mobiles hautement qualifiées (infirmières autorisées) /infirmières praticiennes autorisées (infirmières praticiennes autorisées) qui peuvent administrer la collecte d''échantillons de PCR et les tests POC à l''endroit assigné.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.803' AS DateTime))
