INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (232, N'HavingAnAppointmentIsRecommended', N'Avoir un rendez-vous est recommandé et vous pouvez cliquer ici pour réserver votre test. Cependant, si vous êtes parent avec un enfant à l''école ou employé d''une entreprise qui effectue des tests couverts, vous pouvez simplement vous rendre sur le site de test. Vous pouvez donner votre consentement et vous faire tester ou votre enfant le jour même.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.803' AS DateTime))
