INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (234, N'AnAthomeTestForAn', N'Un test à domicile pour un client individuel est de 199$ + taxes si vous habitez dans le rayon de 25 km du centre-ville de Toronto. Pour chaque kilomètre supplémentaire en dehors du rayon, il y a un supplément de 0,50 $/km. Si vous cherchez à faire plusieurs tests (milieux de l''entreprise et industriels), veuillez nous contacter par téléphone ou par courriel', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.803' AS DateTime))
