INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (236, N'GvtOfferBothMobilePcr', N'GVT offre à la fois des tests PCR mobiles et des tests POC rapides Les tests PCR (réaction de polymérisation en chaîne) sont administrés sur place par du personnel médical qualifié. Les résultats sont disponibles sur le site Web de cyberSanté Ontario dans les 48 heures. Le test rapide POC (point de soins) permet de déterminer si vous êtes susceptible d''être porteur du virus. Les résultats des tests sont généralement disponibles dans les 2 heures et toute personne ayant un test rapide positif devrait faire un suivi avec un test PCR pour confirmation.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.803' AS DateTime))
