INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (238, N'OnceAsymptomaticTestingIsAvailable', N'Une fois que le test asymptomatique est disponible dans l''école de votre enfant, vous recevrez un courriel expliquant le processus de test. Ce courriel inclura également un lien pour inscrire votre enfant au test et donner votre consentement pour que les résultats soient communiqués à la commission scolaire. Si vous avez des difficultés à vous inscrire ou si vous décidez de faire tester votre enfant au dernier moment, vous pouvez tout de même faire le test. Il suffit de venir sur le site de test avec votre enfant — vous pouvez signer le formulaire de consentement sur place.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.803' AS DateTime))
