INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (240, N'GvtLabAndImagingServices', N'GVT Lab & Imaging Services est fière de travailler avec le gouvernement de l''Ontario et le ministère de l''Éducation de l''Ontario pour effectuer des tests de dépistage de la Covid-19 dans les écoles de la province. GVT a fait ses preuves en travaillant avec des enfants et l''adolescents tout en les gardant à l''aise et en aidant leurs parents à prendre des décisions éclairées.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.803' AS DateTime))
