INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (261, N'ForBusinessTransfersweMayUse', N'Pour les transferts d''entreprises : Nous pouvons utiliser vos informations pour évaluer ou effectuer une fusion, une cession, une restructuration, une réorganisation, une dissolution ou une autre vente ou transfert de tout ou partie de nos actifs, que ce soit dans le cadre d''une faillite, d''une liquidation ou d''une procédure similaire, dans laquelle les données personnelles détenus par Nous au sujet de nos utilisateurs de Service font partie des actifs transférés.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.807' AS DateTime))
