INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (263, N'WithServiceProvidersweMayShare', N'Avec les fournisseurs de services : Nous pouvons partager vos informations personnelles avec les fournisseurs de services pour surveiller et analyser l''utilisation de notre service, pour vous contacter.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.810' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (264, N'ForBusinessTransfersweMayShare', N'Pour les transferts d''entreprise : Nous pouvons partager ou transférer vos renseignements personnels dans le cadre ou pendant les négociations de toute fusion, vente d''actifs de la société, financement ou acquisition de tout ou partie de nos activités à une autre société.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.810' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (265, N'WithAffiliatesweMayShareYour', N'Avec les sociétés affiliées : Nous pouvons partager vos informations avec nos sociétés affiliées, auquel cas nous demanderons à ces sociétés d''honorer la présente Politique de confidentialité. Les sociétés affiliées comprennent notre société mère et toutes autres filiales, partenaires de société en participation ou autres sociétés que Nous contrôlons ou qui sont sous un contrôle commun avec Nous.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.810' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (266, N'WithBusinessPartnersweMayShare', N'Avec des partenaires commerciaux : Nous pouvons partager Vos informations avec Nos partenaires commerciaux afin de Vous proposer certains produits, services ou promotions.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.810' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (267, N'WithOtherUserswhenYouShare', N'Avec d''autres utilisateurs : Lorsque vous partagez des informations personnelles ou interagissez autrement dans les espaces publics avec d''autres utilisateurs, ces informations peuvent être consultées par tous les utilisateurs et peuvent être diffusées publiquement à l''extérieur. Si vous interagissez avec d''autres utilisateurs ou vous inscrivez via un service de médias sociaux tiers, vos contacts sur le service de médias sociaux tiers peuvent voir Votre nom, profil, photos et description de votre activité. De même, d''autres utilisateurs pourront consulter les descriptions de Votre activité, communiquer avec Vous et voir Votre profil.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.810' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (268, N'WithYourConsentweMayDisclose', N'Avec Votre consentement : Nous pouvons divulguer Vos renseignements personnels à toute autre fin avec Votre consentement. Conservation de Vos Données personnelles La Société conservera Vos Données personnelles uniquement aussi longtemps que nécessaire aux fins énoncées dans la présente Politique de confidentialité. Nous conserverons et utiliserons vos données personnelles dans la mesure nécessaire pour nous conformer à nos obligations légales (par exemple, si nous sommes tenus de conserver vos données pour se conformer aux lois applicables), résoudre les litiges et faire respecter nos accords et politiques juridiques. La Société conservera également les Données d''Utilisation à des fins d''analyse interne. Les données d''utilisation sont généralement conservées pour une période plus courte, sauf lorsque ces données sont utilisées pour renforcer la sécurité ou améliorer la fonctionnalité de notre service, ou si nous sommes légalement tenus de conserver ces données pendant de plus longues périodes. Transfert de vos données personnelles Vos informations, y compris les données personnelles, sont traitées dans les bureaux d''exploitation de la Société et dans tout autre lieu où se trouvent les parties impliquées dans le traitement. Cela signifie que ces informations peuvent être transférées — et maintenues sur — des ordinateurs situés en dehors de Votre État, province, pays ou autre juridiction gouvernementale où les lois sur la protection des données peuvent différer de celles de Votre juridiction. Votre consentement à la présente Politique de confidentialité suivie de Votre soumission de telles informations représente Votre accord à ce transfert. La Société prendra toutes les mesures raisonnablement nécessaires pour s''assurer que Vos données sont traitées en toute sécurité et conformément à la présente Politique de confidentialité et qu''aucun transfert de Vos Données personnelles n''aura lieu à une organisation ou à un pays à moins qu''il n''y ait des contrôles adéquats en place, y compris la sécurité de Vos données et d''autres informations personnelles. Divulgation de vos données personnelles Transactions commerciales Si la Société est impliquée dans une fusion, une acquisition ou une vente d''actifs, Vos Données personnelles peuvent être transférées. Nous vous informerons avant le transfert de vos données personnelles et qu''elles deviennent soumises à une politique de confidentialité différente. Application de la loi Dans certaines circonstances, la Société peut être tenue de divulguer Vos Données personnelles si la loi l''exige ou en réponse à des demandes valides des autorités publiques (par exemple, un tribunal ou un organisme gouvernemental). Autres exigences légales La Société peut divulguer Vos Données Personnelles en croyant de bonne foi qu''une telle action est nécessaire pour : Se conformer à une obligation légale Protéger et défendre les droits ou les biens de la Société Prévenir ou enquêter sur d''éventuels actes répréhensibles en relation avec le Service Protéger le personnel sécurité des Utilisateurs du Service ou du public Protéger contre la responsabilité légale', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.810' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (269, N'SecurityOfYourPersonalData', N'Utilisation de vos données personnelles La sécurité de Vos Données personnelles est importante pour Nous, mais n''oubliez pas qu''aucune méthode de transmission sur Internet, ou méthode de stockage électronique n''est sécurisée à 100 %. Bien que nous nous efforcions d''utiliser des moyens commercialement acceptables pour protéger vos données personnelles, nous ne pouvons pas garantir leur sécurité absolue.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.810' AS DateTime))
