INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (372, N'Covid19IsAnInfectiousIllness', N'La COVID-19 est une maladie infectieuse causée par un coronavirus nouvellement découvert. Pour beaucoup, la maladie est légère ou ne produit pas de symptômes ; cependant, les personnes âgées, celles qui ont des problèmes médicaux sous-jacents (comme les maladies cardiaques, le diabète, les maladies respiratoires chroniques et le cancer) et celles qui sont constamment en contact étroit avec d''autres personnes sont plus susceptibles de développer une maladie grave qui peut entraîner une hospitalisation ou même la mort. Chez GVT LAB & IMAGING SERVICES INC., nous vous proposons un test COVID-19 (sérologie et/ou PCR) afin de dépister, prévenir et accélérer le rétablissement de notre société.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.823' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (373, N'PrivacyNotice', N'Avis de confidentialité', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.823' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (374, N'WeAreCollectingYourTest', N'Nous recueillons votre échantillon de test et d''autres renseignements personnels afin de déterminer si vous avez la COVID-19 et de vous fournir des informations sur vos résultats. Vos renseignements personnels seront recueillis à des fins d''identification, de correspondance de l''échantillon à la personne et de création d''une étiquette de test. Vous n''êtes pas légalement tenu de fournir ces données, mais si vous ne fournissez pas ces informations, nous ne pouvons pas vous tester. Les seules personnes qui auront accès à des renseignements privés, comme votre nom et des renseignements médicaux, seront GVT LAB & IMAGING SERVICES INC. et, le cas échéant, les entités suivantes : Santé Canada, l''unité locale de santé publique ou leurs entrepreneurs chargés de mener des enquêtes sur les maladies ou d''autres activités de services de santé publique ou d''autres personnes autorisées par la loi. Veuillez noter que GVT LAB & IMAGING SERVICES INC. entreprend les travaux au nom du ministère de l''Éducation et partagera des renseignements agrégés avec le ministère, mais pas des renseignements personnels.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.823' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (375, N'PleaseCarefullyReadAndSign', N'Veuillez lire attentivement et signer le consentement éclairé suivant :', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.823' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (376, N'ThroughYourSignatureBelowYou', N'Au moyen de votre signature ci-dessous, vous certifiez avoir lu et compris les Instructions post-test COVID-19 ci-jointes, et que vous avez eu l''occasion de poser des questions sur le test. Vous avez également été informé des risques et des avantages associés au test, y compris la possibilité d''un léger malaise ou d''un résultat incorrect. Si vous subissez des tests, vous reconnaissez que vous l''avez fait volontairement. Si vous refusez le test, vous comprenez que vous pouvez porter ou transmettre la COVID-19, même si vous n''avez pas de symptômes.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.823' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (377, N'YouAlsoUnderstandThatGvt', N'Vous comprenez également que GVT LAB & IMAGING SERVICES INC. n''est pas responsable des résultats des tests.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.823' AS DateTime))
INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (378, N'IAuthorizeThisCovid19Testing', N'J''autorise cette unité d''analyse du COVID-19 à effectuer la collecte et les tests de dépistage PCR ou de sérologie de la COVID-19, conformément à l''ordre d''un fournisseur médical autorisé, d''un responsable de la santé publique ou d''un employeur.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.823' AS DateTime))
