INSERT [dbo].[Localizations] ([pk], [ResourceId], [Value], [LocaleId], [ResourceSet], [Type], [BinFile], [TextFile], [Filename], [Comment], [ValueType], [Updated]) VALUES (381, N'IUnderstandTheTestingUnit', N'Je comprends que l''unité d''analyse n''agit pas comme mon fournisseur médical, que ces tests ne remplacent pas le traitement par mon médecin, et j''assume l''entière responsabilité de prendre les mesures appropriées en ce qui concerne les résultats de mes tests. J''accepte que je demanderai des conseils médicaux, des soins et des traitements à mon médecin si j''ai des questions ou des préoccupations, ou si mon état s''aggrave.', N'fr', N'CommonSet', N'', NULL, NULL, N'', NULL, 0, CAST(N'2021-03-04T21:31:11.823' AS DateTime))
